package BasicConcept;

public class IncreDecreOperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 12, b = 12;
		
		
		//Original Value
		System.out.println("Value of a:"+ a);
		
		//After increment
		System.out.println("Value of ++a:"+ ++a);
		
		//Original Value b
		System.out.println("Value of b:"+ b);
		//After decrement
		System.out.println("Value of --b:"+ --b);

	}

}
