package BasicConcept;

public class Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=12;
		int b=6;
		
		System.out.println("a + b = "+(a+b));
		System.out.println("a - b = "+(a-b));
		System.out.println("a * b = "+(a*b));
		System.out.println("a / b = "+(a/b));
		
	}

}
